import { UlShowcase } from "./style";

export const ShowCaseUl = ({ products }) => {
  return (
    <UlShowcase>
      {products.length &&
        products.map((cards) => {
          return (
            <li key={cards.id} className="card">
              <figure>{/* <img src={cards.img} alt="" /> */}</figure>

              <div className="content">
                <h3>{cards.name}</h3>
                <span className="type">{cards.category}</span>
                <p>{`R$ ${
                  cards.price.toString().split("").length < 3
                    ? `${cards.price}.00`
                    : cards.price
                }`}</p>
                <button>Adicionar</button>
              </div>
            </li>
          );
        })}
    </UlShowcase>
  );
};
