import styled from "styled-components";
import "../../style/global.css";

export const Header = styled.header`
  width: 100vw;
  height: 80px;
  background-color: var(--grey-0);
  padding: 14px;

  div {
    max-width: 1600px;
    height: 100%;
    margin: 0 auto;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;

    @media (min-width: 600px) {
      flex-direction: row;
      padding: 50px;
    }
  }
`;
