import styled from "styled-components";

export const UlShowcase = styled.ul`
  display: flex;
  gap: 20px;
  padding: 0 10px;
  width: 100%;
  overflow-x: auto;

  .card {
    border: 2px solid var(--gray-100);

    width: 300px;
    height: 346px;
  }

  figure {
    width: 100%;
    height: 162px;

    background-color: var(--grey-0);

    display: flex;
    align-items: center;
    justify-content: center;
  }

  figure img {
    height: 100%;
  }

  .content {
    width: 300px;
    display: flex;
    flex-direction: column;
    gap: 14px;
  }
`;
