import { ShoppingCart } from "./style";

export const CartShow = ({ currentSale }) => {
  return (
    <ShoppingCart>
      <ul>
        {currentSale.length > 0 &&
          currentSale.map((cart) => {
            return <li>{cart.name}</li>;
          })}
      </ul>
      <footer>
        <div>
          <p>Total</p>
          <span>0</span>
        </div>
        <button>Remover todos</button>
      </footer>
    </ShoppingCart>
  );
};
