import { ShoppingCart } from "./style";

export const CartShow = ({ currentSale, cartTotal }) => {
  console.log(cartTotal);
  return (
    <ShoppingCart>
      <header>
        <h3>Carrinho de compras</h3>
      </header>
      <ul>
        {currentSale.length > 0
          ? currentSale.map((cart) => {
              return <li key={cart.id}>{cart.name}</li>;
            })
          : ""}
      </ul>
      <footer>
        <div>
          <p>Total</p>
          <span>{cartTotal}</span>
        </div>
        <button>Remover todos</button>
      </footer>
    </ShoppingCart>
  );
};
