import styled from "styled-components";

export const Section = styled.section`
  width: 100vw;

  padding: 14px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
  gap: 20px;
  height: 80%;
  overflow-y: auto;

  @media (min-width: 600px) {
    padding: 14px 50px;
  }

  @media (min-width: 768px) {
    padding: 14px 100px;
  }
`;
