import "./style/reset.css";
import { useState, useEffect } from "react";
import { LogoHeader } from "./components/header";
import { SectionShow } from "./components/section";
import { ShowCaseUl } from "./components/showcase";
import { CartShow } from "./components/Cart";

const App = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentSale, setCurrentSale] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);

  useEffect(() => {
    fetch(`https://hamburgueria-kenzie-json-serve.herokuapp.com/products`)
      .then((res) => res.json())
      .then((res) => setProducts(res))
      .catch((err) => console.log(err));
  }, []);

  const addCart = (food, price) => {
    console.log(price);
    currentSale.length > 0 &&
      currentSale.filter(
        (elem) => food !== elem && setCurrentSale([...currentSale, food])
      );
    setCartTotal(cartTotal + price);
  };

  return (
    <div className="reset containerMain">
      <LogoHeader />
      <SectionShow>
        <ShowCaseUl products={products} setCurrentSale={addCart} />
        <CartShow currentSale={currentSale} cartTotal={cartTotal} />
      </SectionShow>
    </div>
  );
};

export default App;
